mapper.select:
select sp.prod_id,sp.prod_name, sp.prod_price, sp.disc_price, spi.pack_type, spi.dlvy_type, sop.opt_prod_id, sop.opt_prod_name, sop.opt_price, sop.opt_disc_price from product as sp join product_info as spi on sp.prod_id = spi.prod_id left join option_product as sop on sp.prod_id = sop.prod_id and sp.is_opt = 'Y'
sp.prod_id = #{prod_id}
sp.prod_id = #{prod_id} and sop.opt_prod_id = #{opt_prod_id}

mapper.update:
UPDATE cart
SET qty = #{qty}


        prod_id = #{prod_id}


        opt_prod_id = #{opt_prod_id}

mapper.select:
select prod_id, prod_name, prod_price, disc_price
from product
where prod_id = #{prod_id};

select opt_prod_id, opt_prod_name, opt_price, opt_disc_price
from option_product
where opt_prod_id = #{opt_prod_id};
